import React, {useState} from 'react'

const Question = ({number, question}) => {
    const [showAnswer, setShowAnswer] = useState(false)
    const [userAnswer, setUserAnswer] = useState('')
    const [checking, setChecking] = useState(-1)
    const {prompt, answer} = question;

    const handleSubmit = (e) => {
        e.preventDefault();
        const correct = userAnswer === answer;
        setChecking(correct ? 1 : 0)
    }
    const toggleAnswer = () => {
        setShowAnswer(!showAnswer);
    }

    const type = (e) => {
        setUserAnswer(e.target.value)
        if(checking >= 0)
            setChecking(-1)
    }

    const getClassName = () => {
        if(checking === 1)
            return 'check-right'
        if(checking === 0)
            return 'check-wrong'
        return ''
    }
    const getCheckText = () => {
        if(checking === 1)
            return 'Right'
        if(checking === 0)
            return  'Wrong'
        return 'Check'
    }

    return (
        <div className='question-card'>
            <div className='input-div'>
                <p>{number}. </p>
                <form onSubmit={handleSubmit}>
                    <input
                        type='text'
                        value={userAnswer}
                        onChange={(e) => type(e)}
                        />
                </form>
            </div>
            <div className='question-buttons'>
                <button type='button' className={getClassName()} onClick={handleSubmit}>{getCheckText()}</button>
                <button type='button' onClick={toggleAnswer}>Show</button>
            </div>
            <p>{prompt}</p>
            <p>{showAnswer ? answer : ''}</p>
        </div>
    )
}

export default Question
