import cities from "../../database/cityData";
import { getPerson, getPeople } from "../../database/peopleData";
import { capitalizeFirstLetter } from "../../utils";

const getQuestion = () => {
    const isMale = Math.floor(Math.random() * 2) === 0
    const isPlural = Math.floor(Math.random() * 2) === 0
    let subject;
    if(isPlural)
        subject = getPeople();
    else
        subject = getPerson(isMale);
    const city = cities[Math.floor(Math.random() * cities.length)];
    const prompt = `${subject.firstName} ${!isPlural ? 'is' : 'are'} from ${city}.`
    const answer = capitalizeFirstLetter(`${subject.firstName} ${!isPlural ? "isn't" : "aren't"} from ${city}.`)
    return {prompt, answer}
}

export default getQuestion;
