const maleNames = [
    "Adam",
    "Ashton",
    "Dave",
    "John",
    "Tom",
    "Henry",
    "Mark",
    "Matthew",
    "William",
    "Robert",
    "Carl",
    "Wesley"
]

const femaleNames = [
    "Mary",
    "Susan",
    "Katherine",
    "Olivia",
    "Pamela",
    "Beatrice",
    "Sandra",
    "Diana",
    "Phoebe",
    "Anna",
    "Christine",
    "Lisa",
    "Vanessa"
]

export const GetMale = () => {
    const type = 'male';
    const firstName = maleNames[Math.random() * maleNames.length];
    const personalPronoun = 'he';
    const objectPronoun = 'him';
    const possessivePronoun = 'his';
    return {type, firstName, personalPronoun, objectPronoun, possessivePronoun}
}

export const GetFemale = () => {
    const type = 'female';
    const firstName = maleNames[Math.random() * femaleNames.length];
    const personalPronoun = 'she';
    const objectPronoun = 'her';
    const possessivePronoun = 'her';
    return {firstName, personalPronoun, objectPronoun, possessivePronoun}
}

export const GetPerson = (isMale) => {
    const type = isMale ? 'male' : 'female';
    const firstName = isMale ? 
    maleNames[Math.random() * maleNames.length] : 
    femaleNames[Math.random() * femaleNames.length]; 
    const personalPronoun = isMale ? 'he' : 'she';
    const objectPronoun = isMale ? 'him' : 'her';
    const possessivePronoun = isMale ? 'his' : 'her';
    return {type, firstName, personalPronoun, objectPronoun, possessivePronoun}
}
