import React from 'react'
import Question from './Question';

const Activity = ({activity}) => {
    const {description, example} = activity;
    const questions = [
        {
            prompt: 'prompt',
            answer: 'answer'
        },
        {
            prompt: 'test',
            answer: 'here we go'
        },
    ]
    return (
    <div>
        <h3>{description}</h3>
        <h4>{example.answer} ({example.question})</h4>
        {questions.map((question, index) => {
            <Question number={index + 1} question={question} />
        })}
        
    </div>
    )
}

export default Activity
