import React, {useState} from 'react'

const Question = ({number, question}) => {
    const [userAnswer, setUserAnswer] = useState('')
    const {prompt, answer} = question;

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submit handled")
    }
    return (
        <div className='question-form'>
            <p>{number}. </p>
            <input
                type='text'
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
            />
            <div className='question-form-buttons'>
                <button type='button' onClick={handleSubmit}>Check Answer</button>
                <button type='button' onClick={handleSubmit}>Show Answer</button>
            </div>
            <p>{prompt}</p>
            <p>{answer}</p>
        </div>
    )
}

export default Question
