import React from 'react'
import Activity from './Activity';

const Lesson = ({data}) => {
  const { index, title, activities } = data[0];
  console.log(data)
  return (
    <section>
      <h2>{title}</h2>
      {activities.map((activity) => {
        return <Activity key={index} activity={activity} />
      })}
    </section>
  )
}

export default Lesson
