import countries from "../database/countryData";
import { GetPerson } from "../database/peopleData";
import { capitalizeFirstLetter } from "../utils";

const GetQuestion = () => {
    const gender = Math.floor(Math.random() * 2) === 0
    const subject = GetPerson(gender);
    const country = countries[Math.floor(Math.random() * countries.length)].name;
    const prompt = `${country} / ${subject.firstName}`
    const answer = capitalizeFirstLetter(`${subject.personalPronoun} is from ${country}.`);
    return {prompt, answer}
}

export default GetQuestion;
