import countries from "../database/countryData";
import { GetMale, GetFemale } from "../database/peopleData";
import { capitalizeFirstLetter } from "../utils";

const GetQuestion = () => {
    const gender = Math.floor(Math.random * 2)
    const subject = gender === 0 ? GetMale() : GetFemale();
    const country = Math.floor(Math.random() * countries.length)
    const prompt = `${country} / ${subject.type}`
    const answer = `${subject.personalPronoun} is from ${country}.`;
    answer = capitalizeFirstLetter(answer);
    return {prompt, answer}
}

export default GetQuestion;
