import React, {useState} from 'react'

const Question = ({number, question}) => {
    const [showAnswer, setShowAnswer] = useState(false)
    const [userAnswer, setUserAnswer] = useState('')
    const [checking, setChecking] = useState(-1)
    const {prompt, answer} = question;

    const handleSubmit = (e) => {
        e.preventDefault();
        const correct = userAnswer === answer;
        setChecking(correct ? 1 : 0)
    }
    const toggleAnswer = () => {
        setShowAnswer(!showAnswer);
    }

    return (
        <div className='question-form'>
            <div className='input-div'>
                <p>{number}. </p>
                <form onSubmit={handleSubmit}>
                    <input
                        type='text'
                        value={userAnswer}
                        onChange={(e) => setUserAnswer(e.target.value)}
                        />
                </form>
            </div>
            <div className='question-buttons'>
                <button type='button' className='check-right' onClick={handleSubmit}>Check</button>
                <button type='button' onClick={toggleAnswer}>Show</button>
            </div>
            <p>{prompt}</p>
            <p>{showAnswer ? answer : ''}</p>
        </div>
    )
}

export default Question
