import { facilities } from "../../database/facilitiesData";
import { cities } from "../../database/cityData";
import { getRandomElement, getBool } from "../../utils"

const getQuestion = () => {
    const isPlural = getBool()
    const verb = isPlural ? 'are' : 'is'
    const city = getRandomElement(cities)
    const facility = getRandomElement(facilities)
    const prompt = `${isPlural ? 'are' : 'is'} ${facility}${isPlural ? 's' : ''}.`
    const answer = `${isPlural ? "These" : "This"}`

    return { prompt, answer }
}

export default getQuestion
