import countries from "../database/countryData";
import { GetPerson } from "../database/peopleData";
import { capitalizeFirstLetter } from "../utils";

export const getQuestion = () => {
    const gender = Math.floor(Math.random() * 2) === 0
    const subject = GetPerson(gender);
    const country = countries[Math.floor(Math.random() * countries.length)];
    const prompt = `${country.name} / ${subject.firstName}`
    const answer = capitalizeFirstLetter(`${subject.subjectivePronoun} is ${country.nationality}.`);
    return {prompt, answer}
}
