import fromCountry from '../activities/my-country/fromCountry'
import isNationality from '../activities/my-country/isNationality'
import possessiveCountry from '../activities/my-country/possessiveCountry'

const data = [
    {
        index: 0,
        title: 'My Country',
        activities: [
            {
                description: 'Write sentences about countries. Follow the example:',
                example:
                {
                    question: 'Portugal / John',
                    answer: 'He is from Portugal.'
                },
                getQuestion: fromCountry
            },
            {
                description: 'Write sentences about nationality based on the country and gender.',
                example: {
                    question: 'England / Michael',
                    answer: 'He is English.'
                },
                getQuestion: isNationality
            },
            {
                description: 'Write sentences about countries using personal adjectives.',
                example: {
                    question: 'Spain / Mario',
                    answer: 'His country is Spain.'
                },
                getQuestion: possessiveCountry
            }
        ]
    }

]

export default data;
